import React from  'react'
import styled from 'styled-components';
import {NavLink} from 'react-router-dom';
import avatar from '../img/avatar.png';
function Navigation()
{
    return(
        <NavigationStyled>
          <div className="avatar">
          <img src={avatar} alt=""/>


          </div>
          <ul className ="nav-items">
            <li className="nav-item">
                <NavLink to="/home" activeClassName="active-class">Home</NavLink>

                

            </li>
            <li className="nav-item">
                <NavLink to="/about" activeClassName="active-class">About</NavLink>

                

            </li>
            <li className="nav-item">
                <NavLink to="/resume" activeClassName="active-class">Resume</NavLink>

                

            </li>
            <li className="nav-item">
                <NavLink to="/blogs" activeClassName="active-class">Blogs</NavLink>

                

            </li>
            <li className="nav-item">
                <NavLink to="/contact" activeClassName="active-class">Contact</NavLink>

                

            </li>

          </ul>
          <footer className="footer">
            <p>My Portfolio Website</p>
          </footer>

          </NavigationStyled>
    )
}
const NavigationStyled = styled.nav`
display: flex;
justify-content: space-between;
flex-direction: column;
align-items: center;
height: 100%;
width: 100%;
border-right:5px solid var(--border-color);
.avatar{
  width: 100%;
  border-bottom:3px solid var(--border-color);
  text-align: center;
  padding: 2rem  0;
  img{
    width:70%;
    border-radius: 50%;
    border:8px solid var(--border-color);
    
  }
}
 .nav-items{
   li{
     display: block;
     text-align: center;
     a{
       display: block;
       padding: .2rem 0;
       &:hover{
           cursor: pointer;

       }
       &:before{
         content: "";
         position: absolute;
         bottom: 0;
         left: 0;
         width: 1rem;
         height: 100%;
         background-color:red;
       }
       a:hover::before{
         width:100%;
         height: 100%;

       }

     }
   }

 }
 footer{
    border-top: 2px solid var(--border-color);
    width: 100%;
   p{
     padding: 2rem 0;
     font-size: 1.1rem;
     text-align: center;
     display: block;
   }
 }
`;
export default Navigation;
